function insertArea($datas, $parentId = 0) {
    foreach ($datas as $data) {
        $area = array(
            'default_name' =>  $data[0],
            'default_abbr' =>  $data[1],
            'parent_id'    =>  $parentId,
            'region_id'    =>  $data[2],
            'names'        =>  $data[3]
        );
        $areaId = Mage::getModel('SunshineBiz_Location_Model_Area')->setData($area)->save()->getId();
        if (count($data) > 4) {
            insertArea($data[4], $areaId);
        }
    }
}
